import './Card.css';

const Card = ({ teams, team, index }) => {
  const teamName = {
    name: team.name,
    show: true,
  }

  const teamInfo = [
    {
      venue: team.venue,
      founded: team.founded,
      colors: team.clubColors,
      address: team.address,
      website: team.website,
      phone: team.phone,
      email: team.email,
      show: false,
    }
  ];

  // const toggleTeamInfo = e => {

  // };

  return (
    <section className='cards'>
      <button className='show'>Info</button>
        <p onClick={() => toggleTeamInfo()}>{teamName.name}</p>
      <div className='show' onClick={() => toggleTeamInfo()}>
        <div className='team-info'>
          {Object.entries(teamInfo).map((info, key) => 
          <p key={key}>{info.name}</p>)}
        </div>
      </div>
    </section>
  )
};

export default Card;
