import './Searchbar.css';
import Board from './Board';

const Searchbar = ({ fetchTeams, teams }) => {

  const searchLeagues = e => {
    e.preventDefault();
    const keyword = e.target.elements.league.value.toUpperCase();
    fetchTeams(keyword);
  };

  const searchTables = e => {
    e.preventDefault();
    const keyword = e.target.elements.league.value.toUpperCase();
    fetchTeams(keyword);
  };

  return (
    <section>
      <form id='form' method="post" onSubmit={searchLeagues}>
        <input type="text" name="league" placeholder="Search league..." />
        <input type="submit" value="Search" />
      </form>
      <section>
        <button onClick={() => searchTables}>Premier League</button>
        <button onClick={() => searchTables}>La Liga</button>
        <button onClick={() => searchTables}>Serie A</button>
        <button onClick={() => searchTables}>1. Bundesliga</button>
      </section>
      {teams.map((team, key) => <Board teams={teams} team={team} index={key} key={key}/>)}
    </section>
  )
};

export default Searchbar;
