import './Card.css';
import { useState } from 'react';

const Card = ({ team }) => {
  const [info, setInfo] = useState([]);
  const teamInfo = [Object.entries(team)];
  const getTeamInfo = id => {
    setInfo(teamInfo);
    return (
    <div>
      <p>{info.name}</p>
      <p>{info.venue}</p>
    </div>
    )
  };

  return (
    <section className='cards'>
      <div onClick={() => getTeamInfo(team)}>{team.name}</div>
    </section>
  )
};

export default Card;
