import './Card.css';

const Card = ({ teams, team, index, toggleTeamInfo }) => {

  return (
    <section className='cards'>
      <div className={teams[index].show ? 'show' : 'hide'}>
        {team.name}
      </div>
      <div className={teams[index].show ? 'hide' : 'show'}>
        {teams[index].venue}
      </div>
      <button onClick={() => toggleTeamInfo(index)} className='cards__show-button'>Info</button>
    </section>
  )
};

export default Card;
