import './Card.css';
import { useState } from 'react';

const Card = ({ teams, team, index }) => {
  const [isActive, setActive] = useState('false');

  const toggleTeamInfo = () => {
    if (isActive) {
      setActive(true);
    }
  };

  return (
    <section className='cards'>
      <div className={isActive ? 'show' : 'hide'}>
        {team.name}
      </div>
      <div className={isActive ? 'hide' : 'show'}>
        {teams[index].venue}
      </div>
      <button onClick={() => toggleTeamInfo(index)} className='cards__show-button'>Info</button>
    </section>
  )
};

export default Card;
