import './Card.css';

const Card = ({ teams, team, index }) => {
  const teamName = {
    name: team.name,
    show: true,
  }

  const teamInfo = [
    {
      venue: team.venue,
      founded: team.founded,
      colors: team.clubColors,
      address: team.address,
      website: team.website,
      phone: team.phone,
      email: team.email,
      show: false,
    }
  ];

  const toggleTeamInfo = index => {
    if (teams[index].show) {
      teams[index].show = false;
    } else {
      teams[index].show = true;
    }
  };

  return (
    <section className='cards'>
      <p className='show' onClick={() => toggleTeamInfo(index)}>{teamName.name}</p>
      <div className='hide' onClick={() => toggleTeamInfo(index)}>
        <div className='team-info'>
        {Object.entries(teamInfo).map((info, key) => 
        <p key={key}>{info.name}</p>)}
        </div>
      </div>
    </section>
  )
};

export default Card;
