import './App.css';
import { useState } from 'react';
import Searchbar from './components/Searchbar';

function App() {
  const [teams, setTeams] = useState([]);
  const [tables, setTables] = useState([]);

  const fixTables = data => {
    const fix1 = {...data.standings};
    const fix2 = Object.values({...fix1['0']});
    console.log(fix2);
  };

  const fetchTeams = id => {
    fetch(`/leagues/${id}`)
        .then(res => res.json())
        .then(data => setTeams(data));
  };

  const fetchTables = id => {
    fetch(`/standings/${id}`)
        .then(res => res.json())
        .then(data => {
          const fix1 = {...data.standings};
          const fix2 = Object.values({...fix1['0']});
          console.log(fix2);
          setTables(fix2)
        });
  };

  return (
    <main className='app'>
      <header className='header__title'>
        <h1>Fotibol</h1>
      </header>
      <Searchbar 
        fetchTeams={fetchTeams}
        fetchTables={fetchTables}
        teams={teams}
        tables={tables}
      />
      <footer className='footer' ></footer>
    </main>
  );
}

export default App;
