import './Card.css';

const Card = ({ team }) => {
  const toggleTeamInfo = (e) => {
    console.log(e.target);
    const id = e.target;
    const sibling = id.nextElementSibling ? id.nextElementSibling : id.previousElementSibling;
    id.classList.contains('show') ? id.className='hide' : id.className='show';
    sibling.classList.contains('show') ? sibling.className='hide' : sibling.className='show';
  };

  return (
    <section className='cards'>
      <p className='show' onClick={(e) => toggleTeamInfo(e)}>{team.name}</p>
      <div className='hide' onClick={(e) => toggleTeamInfo(e)}>
        <div className='team-info'>
          <p>{team.venue}</p>
        </div>
      </div>
    </section>
  )
};

export default Card;
