import './App.css';
import { useState } from 'react';

function App() {
  const [teams, setTeams] = useState([]);

  const fetchTeams = (id) => {
    fetch(`/competitions/${id}`)
        .then(res => res.json())
        .then(data => setState(data));
  }

  return (
    <section className='app'>
      <Searchbar 
        fetchTeams={fetchTeams}
        state={state}
      />
      <button onClick={() => fetchTeams('BL1')}>GO</button>
      {state.map((team, i) => <div>{team.name}</div>)}
    </section>
  );
}

export default App;
