import './Searchbar.css';
import Board from './Board';
import Standings from './Standings';

const Searchbar = ({ fetchTeams, fetchTables, teams, standings, setStandings }) => {

  const searchLeagues = e => {
    e.preventDefault();
    const keyword = e.target.elements.league.value.toUpperCase();
    fetchTeams(keyword);
  };

  const searchTables = e => {
    e.preventDefault();
    const keyword = e.target.name;
    console.log(keyword);
    fetchTables(keyword);
  };

  return (
    <section>
      <form id='form' method="post" onSubmit={searchLeagues}>
        <input type="text" name="league" placeholder="Search league..." />
        <input type="submit" value="Search" />
      </form>
      <section>
        <button name='PL' type='submit' onClick={(e) => searchTables(e)}>Premier League</button>
        <button name='PD' type='submit' onClick={(e) => searchTables(e)}>La Liga</button>
        <button name='SA' type='submit' onClick={(e) => searchTables(e)}>Serie A</button>
        <button name='BL1' type='submit' onClick={(e) => searchTables(e)}>1. Bundesliga</button>
      </section>
      {teams.map((team, key) => <Board teams={teams} team={team} index={key} key={key}/>)}
      <Standings standings={standings}/>
    </section>
  )
};

export default Standings;
