import './Card.css';

const Card = ({ teams, team, index }) => {

  const toggleTeamInfo = i => {
    const name = document.querySelectorAll('.name');
    const info = document.getElementsByClassName('.info');
    console.log(name.class, info.class);
  };

  return (
    <section className='cards'>
      <button onClick={toggleTeamInfo(index)} className='cards__show-button'>Info</button>
      <div className='name'>
        <p>{team.name}</p>
      </div>
      <div className='info hide'>
        <p>{team.venue}</p>
        <p>{team.founded}</p>
        <p>{team.clubColors}</p>
        <p>{team.website}</p>
        <p>{team.email}</p>
        <p>{team.phone}</p>
        <p>{team.address}</p>
      </div>
    </section>
  )
};

export default Card;
