import './Board.css';
import Card from './Card';

const Board = ({ teams, toggleButton }) => {
  const checkHide = id => {
    console.log(toggleButton);
    if (toggleButton[id]) {
      return '';
    } else {
      return 'hide';
    }
  };

  return (
    <>
    <article className=''>
    {teams.map((team, key) => <Card teams={teams} team={team} index={key} key={key}/>)}
    </article>
    </>
  )
};

export default Board;
