import './Board.css';
import Card from './Card';
import Table from './Table';
import Matches from './Matches';
import { useState } from 'react';

const Board = ({ teams, tables, matches }) => {
  const [infoButton, setInfoButton] = useState('false');
  const [tableButton, setTableButton] = useState('true');

  const toggleHideInfo = () => {
    if (infoButton) {
      setInfoButton(false);
    } else {
      setInfoButton(true);
    }
  };

  const toggleHideTable = () => {
    if (tableButton) {
      setTableButton(false);
    } else {
      setTableButton(true);
    }
  };

  const toggleHideMatch = () => {
    if (tableButton) {
      setTableButton(false);
    } else {
      setTableButton(true);
    }
  };

  return (
    <>
      <section className='cards__info-buttons'>
        <button className={infoButton ? 'card__info-button--show' : 'card__info-button--hide'} onClick={() => toggleHideInfo()}>Team Info</button>
        <button className={tableButton ? 'card__info-button--show' : 'card__info-button--hide'} onClick={() => toggleHideTable()}>Table</button>
        <button className={matchButton ? 'card__info-button--show' : 'card__info-button--hide'} onClick={() => toggleHideMatches()}>Table</button>
      </section>
      <section className='all-info'>
        <section className={infoButton ? '' : 'hide'}>
          <h1 class='info-header'>Team Info</h1>
          {teams.map((team, key) => <Card teams={teams} team={team} index={key} key={key}/>)}
        </section>
        <section className={tableButton ? '' : 'hide'}>
          <h1 class='info-header'>Table</h1>
          {tables.map((team, key) => <Table teams={teams} team={team} index={key} key={key}/>)}
        </section>
        <section className={matchButton ? '' : 'hide'}>
          <h1 class='info-header'>Next Round</h1>
          {matches.map((match, key) => <Matches teams={teams} match={match} index={key} key={key}/>)}
        </section>
      </section>
    </>
  )
};

export default Board;
