import './Board.css';
import Card from './Card';

const Board = ({ teams, team, index }) => {
  const fetchGames = id => {
    fetch(`/matches/${id}`)
        .then(res => res.json())
        .then(data => setStandings(data));
  };

  return (
    <>
    <article className='cards__c'>
      <Card teams={teams} team={team} index={index}/>
    </article>
    <section><button onClick={() => fetchGames}>Upcoming Matches</button></section>
    </>
  )
};

export default Board;
