import './Card.css';

const Card = ({ team }) => {
  const teamName = {
    name: team.name,
    show: true,
  }

  const teamInfo = [
      team.venue,
      team.founded,
      team.clubColors,
      team.address,
      team.website,
      team.phone,
      team.email,
  ];

  const toggleTeamInfo = e => e.show ? !e.show : e.show;

  return (
    <section className='cards'>
      <p className='show' onClick={() => toggleTeamInfo(teamName)}>{teamName.name}</p>
      <div className='hide' onClick={() => toggleTeamInfo(teamInfo)}>
        <div className='team-info'>
          {teamInfo.map((info, key) => <p key={key}>{info}</p>)}
        </div>
      </div>
    </section>
  )
};

export default Card;
