import './App.css';
import { useState } from 'react';
import Searchbar from './components/Searchbar';

function App() {
  const [teams, setTeams] = useState([]);

  const fetchTeams = id => {
    fetch(`/leagues/${id}`)
        .then(res => res.json())
        .then(data => setTeams(data));
  };

  const toggleTeamInfo = (index) => {
    if (teams[index].show === true) {
      return teams[index].show = false;
    } else {
      return teams[index].show = true;
    }
  };

  return (
    <section className='app'>
      <Searchbar 
        fetchTeams={fetchTeams}
        teams={teams}
        toggleTeamInfo={toggleTeamInfo}
      />
    </section>
  );
}

export default App;
