import './Board.css';
import Card from './Card';

const Board = ({ teams, team, index, toggleButton }) => {
  const checkHide = id => {
    if (toggleButton[id]) {
      return '';
    } else {
      return 'hide';
    }
  };

  return (
    <>
    <article className={checkHide('info')}>
      <Card teams={teams} team={team} index={index}/>
    </article>
    </>
  )
};

export default Board;
