import './Card.css';

const Card = ({ teams, team, index }) => {

  team.show = true;

  const toggleTeamInfo = (index) => {
    if (teams[index].show === true) {
      teams[index].show = false;
    } else {
      teams[index].show = true;
    }
  };

  return (
    <section className='cards'>
      <div className={teams[index].show ? 'show' : 'hide'}>
        {teams[index].name}
      </div>
      <div className={teams[index].show ? 'hide' : 'show'}>
        {teams[index].venue}
      </div>
      <button onClick={() => toggleTeamInfo(index)} className='cards__show-button'>Info</button>
    </section>
  )
};

export default Card;
