import './Searchbar.css';

const Searchbar = ({ fetchTeams, fetchFixtures, teams }) => {

  const searchLeagues = e => {
    e.preventDefault();
    const keyword = e.target.elements.league.value.toUpperCase();
    fetchTeams(keyword);
  };

  const getFixtures = () => {
    const league = 'PL';
    fetchFixtures(league);
  };

  teams.length > 0 ? console.log('JA') : console.log('NEJ');;

  return (
    <section>
        <form id='form' method="post" onSubmit={searchLeagues}>
          <input type="text" name="league" placeholder="Search league..." />
          <input type="submit" value="Search" />
        </form>
        <button onClick={getFixtures()} className={teams.length > 0 ? 'show-fixtures' : 'hide-fixtures'}>Fixtures</button>
      {teams.map((team, i) => <div>{team.name}</div>)}
    </section>
  )
};

export default Searchbar;
