import './Card.css';
import { useState } from 'react';

const Card = ({ team, index }) => {
  const [isActive, setActive] = useState('false');

  const toggleTeamInfo = () => {
    if (isActive === false) {
      setActive(true);
    } else {
      setActive(false);
    }
    console.log(isActive);
  };

  return (
    <section className='cards'>
      <div className={isActive ? 'show' : 'hide'}>
        {team.name}
      </div>
      <div className={isActive ? 'hide' : 'show'}>
        {team.venue}
      </div>
      <button onClick={toggleTeamInfo}>Toggle class</button>
    </section>
  )
};

export default Card;
