import './Searchbar.css';
import Board from './Board';

const Searchbar = ({ fetchTeams, fetchTables, teams, tables }) => {

  const searchLeagues = e => {
    e.preventDefault();
    const keyword = e.target.elements.league.value.toUpperCase();
    // if (keyword === 'PREMIER LEAGUE' || 'PREMIERLEAGUE') {
    //   fetchTeams('PL');
    // } else {
    fetchTeams(keyword);
    fetchTables(keyword);
  };

  const test = {...tables.standings};
  console.log('tables before: ', tables);
  if(test['0'].table) {
    console.log('tables after: ', test['0'].table);
  }
  return (
    <section className='searchbar'>
      <form autoComplete="off" id='searchbar__form' method="post" onSubmit={searchLeagues}>
        <input className='searchbar__input' type="text" name="league" placeholder="Search league..." />
        <input type="submit" value="Search" className='searchbar__submit-button'/>
      </form>
      <section className='cards'>
        <Board teams={teams}/>
      </section>
    </section>

  )
};

export default Searchbar;
