import './Board.css';
import Card from './Card';
import { useState } from 'react';

const Board = ({ teams, team, index }) => {
  const [games, setGames] = useState([]);

  const fetchGames = id => {
    fetch(`/matches/${id}`)
        .then(res => res.json())
        .then(data => setGames(data));
  };

  return (
    <>
    <article className='cards__c'>
      <Card teams={teams} team={team} index={index}/>
    </article>
    <section><button onClick={() => fetchGames('PL')}>Upcoming Matches</button></section>
    {games.map(game => <div>{game.currentMatchday}</div>)}
    </>
  )
};

export default Board;
