import './Board.css';
import Card from './Card';
import Table from './Table';
import { useState } from 'react';

const Board = ({ teams, tables }) => {
  const [infoButton, setInfoButton] = useState('false');
  const [tableButton, setTableButton] = useState('false');

  const toggleHideInfo = () => {
    if (infoButton) {
      setInfoButton(false);
    } else {
      setInfoButton(true);
    }
  };

  const toggleHideTable = () => {
    if (tableButton) {
      setTableButton(false);
    } else {
      setTableButton(true);
    }
  };

  return (
    <>
      <section className='cards__buttons--show cards__buttons--hide'>
        <button className='' onClick={() => toggleHideInfo()}>Team Info</button>
        <button className='' onClick={() => toggleHideTable()}>Table</button>
      </section>
      <section className={infoButton ? 'hide' : ''}>
        {teams.map((team, key) => <Card teams={teams} team={team} index={key} key={key}/>)}
      </section>
      <section className={tableButton ? 'hide' : ''}>
        {tables.map((team, key) => <Table teams={teams} team={team} index={key} key={key}/>)}
      </section>
    </>
  )
};

export default Board;
