import './Card.css';

const Card = ({ team }) => {
  const toggleTeamInfo = (e) => {
    const id = e.target;
    const allTeams = document.querySelectorAll('.show');
    Object.entries(allTeams).map(el => el.classList.remove('show'));
    id.classList.contains('show') ? id.className='hide' : id.className='show';
  };

  return (
    <section className='cards'>
      <p className='show' onClick={(e) => toggleTeamInfo(e)}>{team.name}</p>
    </section>
  )
};

export default Card;
