import './Card.css';

const Card = ({ team }) => {
  const teamName = {
    name: team.name,
    show: true,
  }

  const teamInfo = [
    {
      venue: team.venue,
      founded: team.founded,
      colors: team.clubColors,
      address: team.address,
      website: team.website,
      phone: team.phone,
      email: team.email,
    }
  ];

  const toggleTeamInfo = e => e.show ? !e.show : e.show;

  return (
    <section className='cards'>
      <p className={teamName.show ? 'show' : 'hide'} onClick={() => toggleTeamInfo(teamName)}>{teamName.name}</p>
      <div className={teamInfo.show ? 'show' : 'hide'} onClick={() => toggleTeamInfo(teamInfo)}>
        <div className='team-info'>
          {teamInfo.map((info, key) => <p key={key}>{info}</p>)}
        </div>
      </div>
    </section>
  )
};

export default Card;
