import './Board.css';
import Card from './Card';

const Board = ({ teams }) => {
  const toggleButton = {};

  const checkHide = id => {
    console.log('toggle', toggleButton[id]);
    if (toggleButton[id]) {
      return '';
    } else {
      return 'hide';
    }
  };

  const toggleHide = id => {
    if (toggleButton[id]) {
      toggleButton[id] = false;
    } else {
      toggleButton[id] = true;
    }
  };

  return (
    <>
      <section className='cards__buttons--show cards__buttons--hide'>
        <button className='' onClick={() => toggleHide('info')}>Info</button>
        <button className='' onClick={() => toggleHide('standings')}>Standings</button>
        <button className='' onClick={() => toggleHide('fixtures')}>Fixtures</button>
      </section>
      <article className={checkHide('info')}>
      {teams.map((team, key) => <Card teams={teams} team={team} index={key} key={key}/>)}
      </article>
    </>
  )
};

export default Board;
