import './Card.css';
import { useState } from 'react';

const Card = ({ team, index }) => {
  const [isActive, setActive] = useState('false');

  const toggleTeamInfo = () => {
    if (isActive === false) {
      setActive(true);
    } else {
      setActive(false);
    }
    console.log(isActive);
  };

  return (
    <section className='cards'>
      <article>
        <div className={isActive ? 'show' : 'hide'}>
          {team.name}
        </div>
      </article>
      <article>
        <div className={isActive ? 'hide cards__info' : 'show'}>
          {team.name}
          {team.venue}
          {team.founded}
          {team.clubColors}
          {team.website}
          {team.address}
          {team.phone}
          {team.email}
        </div>
      </article>
      <button className='card__info-buttonn' onClick={toggleTeamInfo}>Info</button>
    </section>
  )
};

export default Card;
