import './Board.css';
import Card from './Card';
import Table from './Table';
import { useState } from 'react';

const Board = ({ teams, tables }) => {
  const [toggleButton = false, setToggleButton] = useState('false');

  const entries = Object.values({...tables['0']}) || [];
  const toggleHide = () => {
    if (toggleButton) {
      setToggleButton(false);
    } else {
      setToggleButton(true);
    }
  };

  return (
    <>
      <section className='cards__buttons--show cards__buttons--hide'>
        <button className='' onClick={() => toggleHide('info')}>Info</button>
      </section>
      <section className={toggleButton ? '' : 'hide'}>
        {teams.map((team, key) => <Card teams={teams} team={team} index={key} key={key}/>)}
      </section>
      <section>
        {entries > 3 ? entries[3].map((entry, key) => <Table entry={entry} index={key} key={key}/>): null}
      </section>
    </>
  )
};

export default Board;
