import './App.css';
import { useState } from 'react';
import Searchbar from './components/Searchbar';

function App() {
  const [teams, setTeams] = useState([]);

  const fetchTeams = (id) => {
    fetch(`/competitions/${id}`)
        .then(res => res.json())
        .then(data => setTeams(data).toString());
  }

  return (
    <section className='app'>
      <header>
      <Searchbar 
        fetchTeams={fetchTeams}
        teams={teams}
      />
      </header>
      <button onClick={() => fetchTeams('BL1')}>GO</button>
      {teams.map((team, i) => <div>{team.name}</div>)}
    </section>
  );
}

export default App;
