import './App.css';
import { useState } from 'react';

function App() {
  const [state, setState] = useState([]);

  const fetchTeams = (id) => {
    fetch(`/competitions/${id}`)
        .then(res => res.json())
        .then(data => setState(data));
  }

  return (
    <section>
      <button onClick={() => fetchTeams()}>GO</button>
      {state.map((home, i) => <div>{home.name}</div>)}
    </section>
  );
}

export default App;
