const Searchbar = ({ fetchTeams, fetchTeam, teams }) => {
  const searchLeagues = (e) => {
    e.preventDefault();
    const keyword = e.target.elements.keyword.value.toUpperCase();
    fetchTeams(keyword);
  }

  const searchTeams = (e) => {
    e.preventDefault();
    const keyword = e.target.elements.keyword.value.toUpperCase();
    fetchTeam(keyword);
  }

  return (
    <section>
        <form method="post" onSubmit={searchLeagues}>
          <input type="text" name="keyword" placeholder="Search league..." />
          <input type="submit" value="Search" />
        </form>
        <form method="post" onSubmit={searchTeams}>
          <input type="text" name="keyword" placeholder="Search team..." />
          <input type="submit" value="Search" />
        </form>
      {teams.map((team, i) => <div>{team.name}</div>)}
    </section>
  )
};

export default Searchbar;
